	public short getHint() {
		int x = super.getPileSize(0);
		int y = super.getPileSize(1);
		int z = super.getPileSize(2);
		return winConfigs[x][y][z];
	}
	
	public NimGame(int piles, int minAmt, int mxAmt, int lvl) {
		super(piles, minAmt, mxAmt, lvl);
		super.setMultHeaps(false);
	}
	//returns true if move is valid
	public boolean validMove(int pile[], int amt) {
		return pile[0] < super.getPiles().length && amt <= 3 && amt > 0 && amt <= super.getPileSize(pile[0]);
	}
	//AI does the optimum move if there is one.  random move otherwise
	//returns the short representation of the move
	public short eliteMove() {
		int x = super.getPileSize(0);
		int y = super.getPileSize(1);
		int z = super.getPileSize(2);
		short bMove = winConfigs[x][y][z];
		int pile, amt;
		if(bMove==-1) {
			return randomMove();
		}
		else {
			pile = bMove / 10; //0 = smallest pile, 1 = second smallest, etc. (b/c piles were sorted in calculation)
			amt = bMove % 10;
			//super.take(pile, amt);
		}
		return (short) ((10*pile) + amt);
	}
	
	//AI finds a random valid move and does it.  Returns the short representation
	//of the move
	private short randomMove() {
		int pile = (int)(Math.random()*super.getPiles().length);
		while(super.getPileSize(pile)==0) //pile contains 0 stones
			pile = (pile+1) % super.getPiles().length; //game == over --> infinite loop
		byte amt = (byte)((Math.random()*Math.min(super.getPileSize(pile), 3.0)) + 1);
		//super.take(pile, amt);
		return (byte)(pile*10 + amt);
	}
	//consumes a difficulty level and performs a move accordingly.
	//if total number of stones exceeds the difficulty level, the AI will do a random move
	//if level > total stones, AI will do the optimal move.
	public short aiMove(int lvl) {
		if(lvl >= GameState.ELITE) return eliteMove();
		
		if(super.sumPileSizes() >= lvl)
			return randomMove();
		else {
			return eliteMove();
		}
	}
	
