	public short getHint() {
		int x = super.getPileSize(0);
		int y = super.getPileSize(1);
		int z = super.getPileSize(2);
		return winConfigs[x][y][z];
	}
	
	public NimGame2(int piles, int minAmt, int mxAmt, int lvl) {
		super(piles, minAmt, mxAmt, lvl);
		super.setMultHeaps(true);
	}
	public boolean validMove(int[] piles, int amt) {
		boolean b = true;
		for(int pile: piles)
			b = b && (pile < super.getPiles().length && amt <= 3 && amt > 0 && amt <= super.getPileSize(pile));
		return b;
	}
	
	/**
	 * AI does optimal move (if there is one).
	 * Returns the short representation of the move
	*/
	public short eliteMove() {
		int x = super.getPileSize(0);
		int y = super.getPileSize(1);
		int z = super.getPileSize(2);
		short bMove = winConfigs[x][y][z];
		int amt;
		if(bMove==-1) {
			return randomMove();
		}
		else {
			int pile1 = bMove / 1000;
			int pile2 = (bMove - 1000*pile1) / 100;
			int pile3 = (bMove - 1000*pile1 - 100*pile2) / 10;
			amt = bMove % 10;
			//super.take(new int[] { pile1 - 1, pile2 - 1, pile3 - 1 }, amt);
		}
		return bMove;
	}
	/**
	 * AI does random move.  Returns short representation of the move
	 */
	public short randomMove() {
		int length = super.getPiles().length;
		boolean[] pileBools = new boolean[length];
		int count = 0;
		for (int i=0; i<pileBools.length; i++) {
			pileBools[i] = randomBoolean() && super.getPileSize(i) > 0;
			if(pileBools[i]) count++;
		}
		if(count == 0) { //randomly take from a single pile
			int pile, amt;
			pile = (int)(Math.random()*length);
			while(super.getPileSize(pile)==0) //pile contains 0 stones
				pile = (pile+1) % length; //game == over --> infinite loop
			amt = (byte)((Math.random()*Math.min(super.getPileSize(pile), 3.0)) + 1);
			//super.take(pile, amt);
			return (short)(10*(1+pile) + amt);
		}
		else {
			int min = 3; //max # stones you can take
			int[] piles = new int[count];
			int index = 0;
			for(int i=0; i<pileBools.length; i++) { 
				if(pileBools[i]) {
					if(super.getPileSize(i) < min) 
						min = super.getPileSize(i);
					piles[index] = i;
					index++;
				}
			}
			int amt = 1 + (int)(Math.random()*min);
			short result = (short) amt;
			for(int i=0; i<piles.length; i++) {
				result += (short) (Math.pow(10, i+1) * (1+piles[i]));
			}
			//super.take(piles,amt);
			return result;
		}
	}
	
	private boolean randomBoolean(){
		return .618 > Math.random();
	}
	
	/**
	 * takes in a short representation of a move, and returns the corresponding
	 * array of piles to take from.  Amt must be found seperately.
	 */
	public static int[] interpMove(short sMove) {
		int[] result = new int[(int)Math.log10(sMove)];
		sMove /= 10;
		for(int i=0; i<result.length; i++) {
			result[i] = sMove % 10 - 1;
			sMove /= 10;
		}
		return result;
	}

	/**
	 * AI moves based on difficulty.
	 * If total stones < lvl --> optimum move
	 * Else --> random move
	 */
	public short aiMove(int lvl) {
		if(lvl >= GameState.ELITE) 
			return eliteMove();
		if(super.sumPileSizes() >= lvl)
			return randomMove();
		else {
			return eliteMove();
		}
	}
	public void aiGo(short move) {
		int amt = move % 10;
		int[] piles = interpMove(move);
		super.take(piles, amt);
	}
